import React, { useRef, useState, useEffect } from "react";
import Carouselmain from "../components/Carouselmain";
import Baner from "../components/Baner";
import Price1 from "../img/price1.jpg";
import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
import Rating from "@material-ui/lab/Rating";
import Barmenu from "../components/Appbar";
import Box from "@material-ui/core/Box";
import Map from "../components/Map";
import CssBaseline from "@material-ui/core/CssBaseline";
import Typography from "@material-ui/core/Typography";
import Container from "@material-ui/core/Container";
import Button from "@material-ui/core/Button";

import logo from "../img/logo.jpg";
import Goodslist from "../components/Goodslist";
import Goodsitemwithprice from "../components/Goodslistwithprice";

const useStyles = makeStyles((theme) => ({
  root: {
    width: 800,
    margin: "50px auto 100px auto",

    "& > *": {
      margin: 0,
      //   backgroundColor: "white",
      width: "100%",
      //   height: 200,
      borderRadius: 0,
    },
  },
  image: {
    width: "100%",
  },
}));

export default function Item() {
  const classes = useStyles();
  return (
    <div className={classes.root}>
      <Grid container spacing={3}>
        <Grid item xs={6}>
          <img className={classes.image} src={Price1} />
        </Grid>
        <Grid item xs={6}>
          <Container>
            <Typography>
              LEGEND PRO / MATTE BLACK AND RED / FIRE MIRROR COLORBOOST
              POLARIZED SMOKE LENS
            </Typography>
            <Box
              style={{
                display: "flex",
                justifyContent: "left",
                margin: "10px 0 10px 0",
              }}
            >
              <Rating name="read-only" value={4} readOnly size="small" />
              <Typography
                variant="h6"
                gutterBottom
                className={classes.text}
                style={{
                  fontSize: "0.7em",
                  width: "auto",
                  marginLeft: "0.3em",
                }}
              >
                43 reviews
              </Typography>
            </Box>
            <Typography>
              See More Clearly with Cassette ColorBoost Lenses!
            </Typography>
            <Button variant="contained" color="secondary">
              ADD TO CART
            </Button>
          </Container>
        </Grid>
        <Grid item xs={12}>
          <Paper className={classes.paper}>xs=3</Paper>
        </Grid>
        <Grid item xs={12}>
          <Paper className={classes.paper}>xs=3</Paper>
        </Grid>
      </Grid>
    </div>
  );
}
