import React from "react";

import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";

import { makeStyles } from "@material-ui/core/styles";
import gallery2 from "../img/gallery2.jpg";

const useStyles = makeStyles((theme) => ({
  root: {
    width: 800,
    margin: "50px auto 100px auto",
    lineHeight: "1.8em",

    "& > *": {
      margin: 0,
      //   backgroundColor: "white",
      width: "100%",
      //   height: 200,
      borderRadius: 0,
    },
  },
  image: {
    width: "100%",
  },
  modal: {
    display: "flex",
    alignItems: "right",
    justifyContent: "right",
    width: "400px",
    backgroundColor: "black",
    color: "white",
  },

  prevprice: {
    color: "red",
    textDecoration: "line-through",
  },
  text: {
    fontSize: "10px",
  },
  buttonquant: {
    minWidth: "0px",
    width: "30px",
    padding: 0,
  },
}));

export default function ModalItem(props) {
  const classes = useStyles();
  return (
    <Grid xs={12} container>
      <Grid xs={3}>
        <img className={classes.image} src={gallery2} />
      </Grid>
      <Grid xs={5}>
        <Typography variant="subtitle1" className={classes.text}>
          Legend PRo/Matte Black and Red/ Fire Mirror/{" "}
        </Typography>
      </Grid>
      <Grid xs={2}>
        <Typography align="center" variant="subtitle1" className={classes.text}>
          {props.price}
        </Typography>
      </Grid>
      <Grid style={{ display: "flex" }} xs={2}>
        <div
          style={{
            display: "flex",
            height: "30px",
            marginTop: "19px",
          }}
        >
          <Button style={{ color: "white" }} className={classes.buttonquant}>
            +
          </Button>
          <Typography>1</Typography>
          <Button style={{ color: "white" }} className={classes.buttonquant}>
            -
          </Button>{" "}
        </div>
      </Grid>
    </Grid>
  );
}
