import React, { useRef, useState, useEffect, useContext } from "react";
import { Link } from "react-router-dom";
import Carouselmain from "../components/Carouselmain";
import Baner from "../components/Baner";
import Price1 from "../img/price1.jpg";
import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
import Rating from "@material-ui/lab/Rating";
import Barmenu from "../components/Appbar";
import Box from "@material-ui/core/Box";
import Map from "../components/Map";
import CssBaseline from "@material-ui/core/CssBaseline";
import Typography from "@material-ui/core/Typography";
import Container from "@material-ui/core/Container";
import Button from "@material-ui/core/Button";
import Modal from "@material-ui/core/Modal";
import Backdrop from "@material-ui/core/Backdrop";
import Fade from "@material-ui/core/Fade";

import logo from "../img/logo.jpg";
import gallery1 from "../img/gallery1.jpg";
import gallery2 from "../img/gallery2.jpg";
import gallery3 from "../img/gallery3.jpg";
import Goodslist from "../components/Goodslist";
import Goodsitemwithprice from "../components/Goodslistwithprice";
import Gallery from "react-grid-gallery";
import Reviews from "../components/Reviews";
import { ContextApp } from "../context/reducer";
import ModalCart from "../modal/ModalCart";
const useStyles = makeStyles((theme) => ({
  root: {
    width: 800,
    margin: "50px auto 100px auto",
    lineHeight: "1.8em",

    "& > *": {
      margin: 0,
      //   backgroundColor: "white",
      width: "100%",
      //   height: 200,
      borderRadius: 0,
    },
  },
  image: {
    width: "100%",
  },
  modal: {
    display: "flex",
    alignItems: "right",
    justifyContent: "right",
    width: "400px",
    backgroundColor: "black",
    color: "white",
  },

  prevprice: {
    color: "red",
    textDecoration: "line-through",
  },
}));

const IMAGES = [
  {
    src: gallery1,
    thumbnail: gallery1,
    thumbnailWidth: 320,
    thumbnailHeight: 150,

    isSelected: true,
    caption: "After Rain (Jeshu John - designerspics.com)",
  },

  {
    src: gallery2,
    thumbnail: gallery2,
    thumbnailWidth: 320,
    thumbnailHeight: 150,

    tags: [
      { value: "Ocean", title: "Ocean" },
      { value: "People", title: "People" },
    ],
    caption: "Boats (Jeshu John - designerspics.com)",
  },

  {
    src: gallery3,
    thumbnail: gallery3,
    thumbnailWidth: 320,
    thumbnailHeight: 150,
  },
  {
    src: gallery1,
    thumbnail: gallery1,
    thumbnailWidth: 320,
    thumbnailHeight: 150,

    isSelected: true,
    caption: "After Rain (Jeshu John - designerspics.com)",
  },
];

const items = {
  title:
    "LEGEND PRO / MATTE BLACK AND RED / FIRE MIRROR COLORBOOST  POLARIZED SMOKE LENS",
  price: 450,
  prevprice: 500,
  id: 21322,
  itemrating: {
    reviews: 43,
    rating: 4,
  },
};

export default function Item() {
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);
  const { state, dispatch } = useContext(ContextApp);

  const [localState, setState] = useState(state);

  console.log(localState);
  const handleAddItem = (event) => {
    dispatch({ type: "add_item", payload: localState });
  };

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleAddtoCart = () => {
    handleOpen();
  };

  return (
    <div className={classes.root}>
      <ModalCart open={open} handleClose={handleClose} />

      <Grid container spacing={3}>
        <Grid item xs={6}>
          <img className={classes.image} src={gallery2} />
        </Grid>
        <Grid item xs={6}>
          <Container>
            <Typography style={{ lineHeight: "1.8em" }}>
              {items.title}
            </Typography>
            <Box
              style={{
                display: "flex",
                justifyContent: "left",
                margin: "10px 0 10px 0",
              }}
            >
              <Rating
                name="read-only"
                value={items.itemrating.rating}
                readOnly
                size="small"
              />
              <Typography
                variant="h6"
                gutterBottom
                className={classes.text}
                style={{
                  fontSize: "0.7em",
                  width: "auto",
                  marginLeft: "0.3em",
                  lineHeight: "1.8em",
                }}
              >
                {items.itemrating.reviews} reviews
              </Typography>
            </Box>
            <Typography style={{ lineHeight: "1.8em" }}>
              See More Clearly with Cassette ColorBoost Lenses!
            </Typography>
            <Typography style={{ lineHeight: "1.8em" }}>
              {items.price}${" "}
              <span>
                <span className={classes.prevprice}> {items.prevprice}$</span>{" "}
              </span>
            </Typography>
            <Button
              onClick={handleOpen}
              variant="contained"
              color="secondary"
              style={{ width: "100%", margin: "10px 0 10px 0" }}
            >
              ADD TO CART
            </Button>
          </Container>
        </Grid>
        <Grid item xs={12}>
          <Container>
            <Typography
              style={{ lineHeight: "1.8em" }}
              variant="body1"
              align="center"
              gutterBottom
            >
              COLOR, CLARITY, CONTRAST
            </Typography>
            <Typography
              style={{ lineHeight: "1.8em" }}
              variant="body2"
              align="center"
              gutterBottom
            >
              The Legend never dies. It is what happens when high performance
              and lifestyle meet.
            </Typography>
            <Typography
              style={{ lineHeight: "1.8em" }}
              variant="body2"
              align="center"
              gutterBottom
            >
              The Legend frames are made from a Dual Injection Mold using
              Grilamid® TR90 Thermoplastic and Performance Grip Rubber. The
              hinges feature our bombproof Cam-Lock design that works perfectly
              day-in and day-out, whether on your face or clipped to your
              T-Shirt.
            </Typography>
            <Typography
              style={{ lineHeight: "1.8em" }}
              variant="body2"
              align="center"
              gutterBottom
            >
              The Legend features our new ColorBoost Lenses. With 2 years of
              development, the Cassette ColorBoost Lenses attenuate certain
              wavelengths of light to equalize, enhance, brighten and give
              contrast to colors while reducing overall light to a comfortable
              level. Better color contrast & recognition gives you more visual
              sensitivity and clarity. Cassette ColorBoost, Superior Color,
              Superior Clarity
            </Typography>
          </Container>
        </Grid>
        <Grid item xs={12}>
          <Gallery images={IMAGES}></Gallery>
        </Grid>
        <Grid item xs={12}>
          <Reviews />
        </Grid>
      </Grid>
    </div>
  );
}
