import React, { useRef, useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import Link from "@material-ui/core/Link";

import IconButton from "@material-ui/core/IconButton";
import MenuIcon from "@material-ui/icons/Menu";
import ShoppingCartIcon from "@material-ui/icons/ShoppingCart";
import ShoppingCartOutlinedIcon from "@material-ui/icons/ShoppingCartOutlined";

const useStyles = makeStyles((theme) => ({
  root: {
    position: "sticky",
    top: 0,
    zIndex: "2",
    // opacity: 0.6,
    flexGrow: 1,
  },
  root2: {
    position: "sticky",
    top: 0,
    zIndex: "2",
    opacity: 0.6,
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
    fontSize: " 2rem",
  },
  app: {
    backgroundColor: "black",
    padding: "0 80px 0 80px",
  },
  button: {
    fontSize: "0.8rem",
  },
}));

export default function Barmenu() {
  const classes = useStyles();
  const prevScroll = useRef(0);
  const [curScroll, setScroll] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const curentScroll = window.scrollY;
      prevScroll.current = curentScroll;
      prevScroll.current == 0 ? setScroll(false) : setScroll(true);
      console.log(prevScroll, curScroll);
    };
    window.addEventListener("scroll", handleScroll, { passive: true });
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, [curScroll]);

  const ScrollNavbar = (props) => {
    // const curScroll = props.scroll;
    if (curScroll) {
      return (
        <div className={classes.root2}>
          <AppBar className={classes.app} position="static">
            <Toolbar>
              <Typography variant="h6" className={classes.title}>
                Meteor Glasses
              </Typography>
              <Button href="#About" className={classes.button} color="inherit">
                About us
              </Button>
              <Button
                href="/Contacts"
                className={classes.button}
                color="inherit"
              >
                <Link to={"/Glass"}>Shop</Link>
              </Button>

              <Link href="/Cart">
                <ShoppingCartOutlinedIcon />
              </Link>
            </Toolbar>
          </AppBar>
        </div>
      );
    }
    return (
      <div className={classes.root1}>
        <AppBar className={classes.app} position="static">
          <Toolbar>
            <Typography variant="h6" className={classes.title}>
              Meteor Glasses
            </Typography>
            <Button href="#About" className={classes.button} color="inherit">
              About us
            </Button>
            <Button href="/Contacts" className={classes.button} color="inherit">
              Contacts
            </Button>
            <Button className={classes.button} color="inherit">
              Login
            </Button>
            <Link href="/Cart">
              <ShoppingCartOutlinedIcon />
            </Link>
          </Toolbar>
        </AppBar>
      </div>
    );
  };
  return <ScrollNavbar scroll={prevScroll.current} />;
}
