import React from "react";

export const ContextApp = React.createContext();

export const initialState = {
  cart: [
    { id: 1, name: "item1", price: 45, quant: 1, total: 45, art: 123 },
    { id: 2, name: "item1", price: 45, quant: 1, total: 45, art: 124 },
    { id: 3, name: "item1", price: 45, quant: 1, total: 45, art: 125 },
  ],
  total: 135,
  order: [],
};

export let itemState = {
  title: "FIRE MIRROR COLORBOOST  POLARIZED SMOKE LENS",
  price: 450,
  art: 125,
  prevprice: 500,
  id: 21322,
  itemrating: {
    total: 43,
    rating: 4,
    comments: [
      {
        date: "22/10 /2020",
        author: "Dan Balan0",
        title: "Rocks",
        rating: 4,
        text:
          "Once I got the legend pro on my hand The glasses feel sturdy and look good. Don't know how well the scratch resistance is yet, but they provided you a lifetime warranty so I'm not too worry about it.",
      },
      {
        date: "22/10 /2020",
        author: "Dan Balan0",
        title: "Rocks",
        rating: 2,
        text:
          "Once I got the legend pro on my hand The glasses feel sturdy and look good. Don't know how well the scratch resistance is yet, but they provided you a lifetime warranty so I'm not too worry about it.",
      },
    ],
  },
};

export const cartReducer = (state, action) => {
  switch (action.type) {
    case "add_item":
      const newitem = {
        ...action.payload,
        quant: 1,
        total: action.payload.price,
      };

      const currart = newitem.art;
      console.log(currart);
      const index = state.cart.findIndex((x) => x.art === currart);
      if (index < 0) {
        return {
          ...state,
          cart: [...state.cart, newitem],
          total: state.total + newitem.price,
        };
      } else {
        state.cart[index].quant = state.cart[index].quant + 1;
        state.cart[index].total =
          state.cart[index].total + state.cart[index].price;
        state.total = state.total + state.cart[index].price;
        return {
          ...state,
        };
      }

    case "add_quant_test":
      const idadd = action.payload;
      state.cart[idadd].quant = state.cart[idadd].quant + 1;
      state.cart[idadd].total =
        state.cart[idadd].total + state.cart[idadd].price;
      state.total = state.total + state.cart[idadd].price;
      console.log(state, "addquant");

      return {
        ...state,
      };

    case "minus_quant_test":
      const idminus = action.payload;
      state.cart[idminus].quant = state.cart[idminus].quant - 1;
      state.cart[idminus].total =
        state.cart[idminus].total - state.cart[idminus].price;
      state.total = state.total - state.cart[idminus].price;
      if (state.cart[idminus] === 0) {
        return state.filter((item) => item.id === idminus);
      }
      console.log(state, "addquant");

      return {
        ...state,
      };

    case "send_order":
      const neworder = {
        num: 1223,
        customer: "oleg",
        items: state.cart,
        total: state.total,
      };

      return {
        ...state,
        cart: [],
        total: 0,
        order: [...state.order, neworder],
      };
    default:
      return state;
  }
};
