import React from "react";

export const ContextApp = React.createContext();

export const initialState = {
  cart: [
    { name: "item1", price: 45, quant: 1, total: 45 },
    { name: "item1", price: 45, quant: 1, total: 45 },
  ],
  total: 90,
};
export const cartReducer = (state, action) => {
  switch (action.type) {
    case "add_item":
      console.log(state, action.payload);
    // //   return {
    // //     ...state,
    // //     ...action.payload,
    //   };
    default:
      return state;
  }
};
