import React, { useRef, useState, useEffect, useContext } from "react";

import { makeStyles } from "@material-ui/core/styles";

import Grid from "@material-ui/core/Grid";

import Typography from "@material-ui/core/Typography";

import Button from "@material-ui/core/Button";

import gallery1 from "../img/gallery1.jpg";

const useStyles = makeStyles((theme) => ({
  root: {
    width: 800,
    textAlign: "center",
    margin: "50px auto 100px auto",

    "& > *": {
      margin: 0,
      //   backgroundColor: "white",
      width: "100%",
      //   height: 200,
      borderRadius: 0,
    },
  },
  item: {
    marginTop: "10px",
  },
  image: {
    width: "100%",
  },
  total: {
    color: "darkred",

    marginTop: "20px",
    backgroundColor: "lightgrey",
  },
  hritem: {
    border: 0,
    height: 0,
    borderTop: "1px solid rgba(0, 0, 0, 0.1)",
    borderBottom: "1px solid rgba(255, 255, 255, 0.3)",
  },
}));

export default function CartItem(props) {
  const classes = useStyles();

  return (
    <Grid className={classes.item} container xs={12} spacing={2}>
      <Grid xs={3}>
        <img className={classes.image} src={gallery1} />
      </Grid>
      <Grid item xs={3}></Grid>
      <Grid item xs={2}>
        <div
          style={{
            display: "flex",
            height: "30px",
          }}
        >
          <Button
            onClick={() => props.handleAddQuant()}
            size="small"
            className={classes.buttonquant}
            id="1"
          >
            +
          </Button>
          <Typography>{props.quant}</Typography>
          <Button
            onClick={() => props.handleMinusQuant()}
            size="small"
            className={classes.buttonquant}
          >
            -
          </Button>{" "}
        </div>
      </Grid>
      <Grid item xs={2}>
        <Typography>{props.price}$</Typography>
      </Grid>
      <Grid item xs={2}>
        <Typography>{props.total}$</Typography>
      </Grid>
      <Grid item xs={12}>
        <hr className={classes.hritem} />
      </Grid>
    </Grid>
  );
}
