import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Image from "material-ui-image";
import Typography from "@material-ui/core/Typography";
import Rating from "@material-ui/lab/Rating";
import Grid from "@material-ui/core/Grid";
import Avatar from "@material-ui/core/Avatar";
import Review from "./Review";

const Reviewsstate = [
  {
    author: "Dan Balan",
    title: "Rockks",
    rating: 4,
    data: "22/10/2020",
    text:
      "Once I got the legend pro on my hand, I totally feel like my previous couple pairs of O brand shades are over priced! So far in a month of usage, I'm pretty happy with the purchase. The glasses feel sturdy and look good. Don't know how well the scratch resistance is yet, but they provided you a lifetime warranty so I'm not too worry about it.",
  },
  {
    author: "Dan Balan",
    title: "Rockks",
    data: "22/10/2020",
    rating: 4,
    text:
      "Once I got the legend pro on my hand, I totally feel like my previous couple pairs of O brand shades are over priced! So far in a month of usage, I'm pretty happy with the purchase. The glasses feel sturdy and look good. Don't know how well the scratch resistance is yet, but they provided you a lifetime warranty so I'm not too worry about it.",
  },
];
const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: "center",
    color: theme.palette.text.secondary,
  },

  text: {
    width: "100%",
    maxWidth: 500,
  },
  image: {
    width: "100%",
  },
}));

export default function Reviews(props) {
  const classes = useStyles();

  return (
    <Grid container spacing={1}>
      {Reviewsstate.map((item) => {
        return <Review item={item} />;
      })}
      <Review />
    </Grid>
  );
}
