import React from "react";
import { act } from "react-dom/test-utils";

export const ContextApp = React.createContext();

export const initialState = {
  cart: [
    { id: 1, name: "item1", price: 45, quant: 1, total: 45, art: 123 },
    { id: 2, name: "item1", price: 45, quant: 1, total: 45, art: 124 },
    { id: 3, name: "item1", price: 45, quant: 1, total: 45, art: 125 },
  ],
  total: 135,
  order: [],
};
export const cartReducer = (state, action) => {
  switch (action.type) {
    case "add_item":
      const newitem = {
        ...action.payload,
        quant: 1,
        total: action.payload.price,
      };
      const currart = action.playload.art;
      const index = state.cart.findIndex((x) => x.art === currart);
      if (index < 0) {
        return {
          ...state,
          cart: [...state.cart, newitem],
          total: state.total + newitem.price,
        };
      } else {
        state.cart[idadd].quant = state.cart[idadd].quant + 1;
        state.cart[idadd].total =
          state.cart[idadd].total + state.cart[idadd].price;
        state.total = state.total + state.cart[idadd].price;
        return {
          ...state,
        };
      }

    case "add_quant_test":
      const idadd = action.payload;
      state.cart[idadd].quant = state.cart[idadd].quant + 1;
      state.cart[idadd].total =
        state.cart[idadd].total + state.cart[idadd].price;
      state.total = state.total + state.cart[idadd].price;
      console.log(state, "addquant");

      return {
        ...state,
      };

    case "minus_quant_test":
      const idminus = action.payload;
      state.cart[idminus].quant = state.cart[idminus].quant - 1;
      state.cart[idminus].total =
        state.cart[idminus].total - state.cart[idminus].price;
      state.total = state.total - state.cart[idminus].price;
      if (state.cart[idminus] === 0) {
        return state.filter((item) => item.id === idminus);
      }
      console.log(state, "addquant");

      return {
        ...state,
      };

    case "send_order":
      const neworder = {
        num: 1223,
        customer: "oleg",
        items: state.cart,
        total: state.total,
      };

      return {
        ...state,
        cart: [],
        total: 0,
        order: [...state.order, neworder],
      };
    default:
      return state;
  }
};
