import React, { useRef, useState, useEffect } from "react";
import Carouselmain from "../components/Carouselmain";
import Baner from "../components/Baner";
import Price1 from "../img/price1.jpg";
import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
import Rating from "@material-ui/lab/Rating";
import Barmenu from "../components/Appbar";
import Box from "@material-ui/core/Box";
import Map from "../components/Map";
import CssBaseline from "@material-ui/core/CssBaseline";
import Typography from "@material-ui/core/Typography";
import Container from "@material-ui/core/Container";
import Button from "@material-ui/core/Button";
import Modal from "@material-ui/core/Modal";
import Backdrop from "@material-ui/core/Backdrop";
import Fade from "@material-ui/core/Fade";

import logo from "../img/logo.jpg";
import gallery1 from "../img/gallery1.jpg";
import gallery2 from "../img/gallery2.jpg";
import gallery3 from "../img/gallery3.jpg";
import Goodslist from "../components/Goodslist";
import Goodsitemwithprice from "../components/Goodslistwithprice";
import Gallery from "react-grid-gallery";
import Reviews from "../components/Reviews";

const useStyles = makeStyles((theme) => ({
  root: {
    width: 800,
    margin: "50px auto 100px auto",

    "& > *": {
      margin: 0,
      //   backgroundColor: "white",
      width: "100%",
      //   height: 200,
      borderRadius: 0,
    },
  },
  item: {
    marginTop: "10px",
  },
  image: {
    width: "100%",
  },
  total: {
    color: "darkred",

    marginTop: "20px",
    backgroundColor: "lightgrey",
  },
  hritem: {
    border: 0,
    height: 0,
    borderTop: "1px solid rgba(0, 0, 0, 0.1)",
    borderBottom: "1px solid rgba(255, 255, 255, 0.3)",
  },
}));

export default function Cart() {
  const classes = useStyles();

  return (
    <div className={classes.root}>
      <Grid container spacing={2}>
        <Grid className={classes.item} container xs={12} spacing={2}>
          <Grid xs={3}>
            <img className={classes.image} src={gallery2} />
          </Grid>
          <Grid item xs={3}></Grid>
          <Grid item xs={2}>
            <Typography>1</Typography>
          </Grid>
          <Grid item xs={2}>
            <Typography>45$</Typography>
          </Grid>
          <Grid item xs={2}>
            <Typography>45$</Typography>
          </Grid>
          <Grid item xs={12}>
            <hr className={classes.hritem} />
          </Grid>
        </Grid>
        <hr className={classes.hritem} />
        <Grid className={classes.item} container xs={12} spacing={2}>
          <Grid xs={3}>
            <img className={classes.image} src={gallery2} />
          </Grid>
          <Grid item xs={3}></Grid>
          <Grid item xs={2}>
            <div
              style={{
                display: "flex",
                height: "30px",
              }}
            >
              <Button size="small" className={classes.buttonquant}>
                +
              </Button>
              <Typography>1</Typography>
              <Button size="small" className={classes.buttonquant}>
                -
              </Button>{" "}
            </div>
          </Grid>
          <Grid item xs={2}>
            <Typography>45$</Typography>
          </Grid>
          <Grid item xs={2}>
            <Typography>45$</Typography>
          </Grid>
          <Grid item xs={12}>
            <hr className={classes.hritem} />
          </Grid>
        </Grid>

        <Grid container className={classes.total} item xs={12}>
          <Grid item xs={8}>
            <Typography style={{ fontSize: "large" }}>Total: 45$</Typography>
          </Grid>
          <Grid item xs={4}>
            <Button
              variant="contained"
              color="secondary"
              style={{ width: "100%" }}
            >
              Send
            </Button>
          </Grid>
        </Grid>
      </Grid>
    </div>
  );
}
