import React from "react";

export const ContextApp = React.createContext();

export const initialState = {
  cart: [
    { id: 1, name: "item1", price: 45, quant: 1, total: 45 },
    { id: 2, name: "item1", price: 45, quant: 1, total: 45 },
    { id: 3, name: "item1", price: 45, quant: 1, total: 45 },
  ],
  total: 135,
};
export const cartReducer = (state, action) => {
  switch (action.type) {
    case "add_item":
      console.log(state, action.payload);

      return {
        ...state,
      };
    case "add_quant":
      console.log(state, "addquant");
      return {
        ...action.payload,
      };
    case "add_quant_test":
      console.log(state, "addquant");
      const currentitem = state.cart[action.payload - 1];
      currentitem.quant = currentitem.quant++;
      currentitem.total = currentitem.total + currentitem.price;
      state.total = state.total + currentitem.price;

      return {
        ...state,
      };

    default:
      return state;
  }
};
